export const directionReducer = (lang = 'en', action) => {
    console.log(lang);
    const dir = 'ltr';

    console.log(action);
     if (action.type === 'CHANGE_LANGUAGE') {
          if ( lang === 'en' ) {
              dir = 'ltr'
          } else {
            dir = 'rtl'
          }
          return dir
      }
    return dir;
};
